#!/usr/bin/env sh

# Synopsis:
# Test the test runner by running it against a predefined set of solutions
# with an expected output.

# Output:
# Outputs the diff of the expected test results against the actual test results
# generated by the test runner.

# Example:
# ./bin/run-tests.sh

exit_code=0

# Iterate over all test directories
for test_dir in tests/*; do
    test_dir_name=$(basename "${test_dir}")
    test_dir_path=$(realpath "${test_dir}")
    input_path="${test_dir_path}/input/"
    results_file_path="${test_dir_path}/results.json"
    expected_results_file_path="${test_dir_path}/expected_results.json"

    slug=$test_dir_name

    echo "TESTING: ${test_dir_name}"
    if [ -f "${test_dir_path}/data.json" ]; then
        slug=$(cat ${test_dir_path}/data.json | jq -r .slug)
    fi

    bin/run.sh "${slug}" "${input_path}" "${test_dir_path}" > /dev/null

    # echo "${test_dir_name}: comparing results.json to expected_results.json"
    diff -urN "${expected_results_file_path}" "${results_file_path}"

    if [ $? -ne 0 ]; then
        exit_code=1
    fi
done

exit ${exit_code}
